import React, {Component } from 'react'

class AllUsers extends Component {

    constructor(props) {
        super(props);
        this.state = {
          items: [],
          isLoaded: false,
        }
      }

      componentDidMount() {
        fetch('https://01vablvh7h.execute-api.us-east-1.amazonaws.com/dev/getallregistereduser')
          .then(response => response.json())
          .then(json => {
            this.setState({
              isLoaded: true,
              items: json,   })
              console.log(json);
          });


      }

    render(){

        var {isLoaded, items} = this.state;

        return(
            <div>
                <h1>HELLO</h1>
                <div id ="allUserstable">
                    <table className="table">
                        <thead className="thead-dark">
                            <tr>
                                <th scope="col">First Name</th>
                                <th scope="col">Last Name</th>
                                <th scope="col">Email</th>
                                <th scope="col">Role</th>
                                <th scope="col">Status</th>
                                <th scope="col">Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {items.body.map(row => {
                                <tr>
                                    <th scope="row" style={{backgroundColor: "whitesmoke"}} key={row.firstName}>{row.firstName}</th>
                                    <td style={{backgroundColor: "whitesmoke"}} key={row.lastName}>{row.lastName}</td>
                                    <td style={{backgroundColor: "whitesmoke"}} key={row.email}>{row.email}</td>
                                    <td style={{backgroundColor: "whitesmoke"}} key={row.role}>{row.role}</td>
                                </tr>

                            })}

                        </tbody>
                    </table>
                </div>

            </div>

            );

    }
}

export default AllUsers;
