import React, { useEffect, useState, } from 'react';
import axios from 'axios';
import "./style.css";


function AllUsers(){




    function deactivateUser(email,){



        let input = {email:email};



          console.log("input: ", input);
          fetch("https://01vablvh7h.execute-api.us-east-1.amazonaws.com/dev/deactiveuser", {
              body: JSON.stringify(input),
              headers: {
                  "Content-Type": "application/json"
              },
              method: "PUT",
              })
              .then(response => response.json())
              .then((res) => {
                  console.log("Response", res);


                  if(res?.statusCode ===200) {


                  }


              })
              .catch((err) => {
                  console.log("###error: ",err);

              });

            }

    function deleteUser(email) {
            console.log("deleted " + email);
            let input = {email:email};

                console.log("input: ", input);
                fetch("https://01vablvh7h.execute-api.us-east-1.amazonaws.com/dev/deleteuser", {
                    body: JSON.stringify(input),
                    headers: {
                        "Content-Type": "application/json"
                    },
                    method: "POST",
                    })
                    .then(response => response.json())
                    .then((res) => {
                        console.log("Response", res);


                        if(res?.statusCode ===200) {


                        }


                    })
                    .catch((err) => {
                        console.log("###error: ",err);

                    });

                }


        const fetchData = async () => {
        const res = await fetch("https://01vablvh7h.execute-api.us-east-1.amazonaws.com/dev/getallregistereduser")
        const json = await res.json()
        return json.result
        }


        const [product, setProduct] = useState([])

        useEffect(() => {
        fetchData().then(product => {
            setProduct(product)
        })
        }, [])



    /*

    const url = "https://01vablvh7h.execute-api.us-east-1.amazonaws.com/dev/getallregistereduser"
    const [product, setProduct] = useState(null);

    let content = null;
    useEffect(() => {
        axios.get(url)
            .then(response => {
                setProduct(response.data)
        })
