import React, { useEffect, useState, } from 'react';
import axios from 'axios';
import "./style.css";
import Button from "./Button"


function AllUsers(){




    function deactivateUser(email,verified){

        if (verified === ' true'){
            console.log(verified)
            console.log("Deactivating " + email);
            alert("Deactivating " + email);
        }
        else {
            console.log(verified);
            console.log("Activating " + email);
            alert("Activating " + email);
        }

        let input = {email:email};



          console.log("input: ", input);
          fetch("https://01vablvh7h.execute-api.us-east-1.amazonaws.com/dev/deactiveuser", {
              body: JSON.stringify(input),
              headers: {
                  "Content-Type": "application/json"
              },
              method: "PUT",
              })
              .then(response => response.json())
              .then((res) => {
                  console.log("Response", res);


                  if(res?.statusCode ===200) {


                  }


              })
              .catch((err) => {
                  console.log("###error: ",err);

              });

            }

    function deleteUser(email) {
            console.log("deleted " + email);
            let input = {email:email};

                console.log("input: ", input);
                fetch("https://01vablvh7h.execute-api.us-east-1.amazonaws.com/dev/deleteuser", {
                    body: JSON.stringify(input),
                    headers: {
                        "Content-Type": "application/json"
                    },
                    method: "POST",
                    })
                    .then(response => response.json())
                    .then((res) => {
                        console.log("Response", res);


                        if(res?.statusCode ===200) {


                        }


                    })
                    .catch((err) => {
                        console.log("###error: ",err);

                    });

                }


    const url = "https://01vablvh7h.execute-api.us-east-1.amazonaws.com/dev/getallregistereduser"
    const [product, setProduct] = useState(null);

    let content = null;
    useEffect(() => {
        axios.get(url)
            .then(response => {
                setProduct(response.data)
        })
    } , [url])



    if(product){
        return (
            <div>
                <table className ="table">
                <thead className="thead-dark">
                    <tr>
                        <th scope="col">First Name</th>
                        <th scope="col">Last Name</th>
                        <th scope="col">Email</th>
                        <th scope="col">Role</th>
                        <th scope="col">Status</th>
                        <th scope="col">Actions</th>
                    </tr>
                </thead>
                <tbody>

                    {product.body.map(user => {
                        if (user.role ==="Admin") {
                            return(
                                <tr>
                                    <th scope="row" style={{backgroundColor: "whitesmoke"}} key={user.firstName}>{user.firstName}</th>
                                    <td key={user.lastName}>{user.lastName}</td>
                                    <td key={user.email}>{user.email}</td>
                                    <td key={user.role}>{user.role}</td>

                                </tr>

                            )
                        }

                        else if (user.adminVerified === 'false'){
                            return(
                                    <tr>
                                        <th scope="row" style={{backgroundColor: "whitesmoke"}} key={user.firstName}>{user.firstName}</th>
                                        <td key={user.lastName}>{user.lastName}</td>
                                        <td key={user.email}>{user.email}</td>
                                        <td key={user.role}>{user.role}</td>
                                        <Button email ={user.email}/>
                                        <td><button onClick= {() => deactivateUser(user.email,user.adminVerified)} style={{color: "red"}} >Unactive</button></td>
                                        <td><button onClick= {() => deleteUser(user.email)} style={{color: "red"}} >Delete</button></td>
                                    </tr>
                                )
                            }
                            else {
                                return(
                                    <tr>
                                        <th scope="row" style={{backgroundColor: "whitesmoke"}} key={user.firstName}>{user.firstName}</th>
                                        <td key={user.lastName}>{user.lastName}</td>
                                        <td key={user.email}>{user.email}</td>
                                        <td key={user.role}>{user.role}</td>
                                        <Button email ={user.email}/>
                                        <td><button onClick= {() => deactivateUser(user.email,user.adminVerified)} style={{color: "green"}} >Active</button></td>
                                        <td><button onClick= {() => deleteUser(user.email)} style={{color: "red"}} >Delete</button></td>
                                    </tr>
                                )
                            }

                        })}

                </tbody>
                </table>
            </div>
        )
    }

    return (

        <div>
            {content}
        </div>
    )


}

export default AllUsers;
