class App extends React.Component {

  state = {
    isLoaded: true,
    posts: [],
    error: null
  }

  fetchUsers(){
    //fetch using getAllRegisteredUser from aws
    fetch('https://01vablvh7h.execute-api.us-east-1.amazonaws.com/dev/getallregistereduser')
    //get response and receive in Json format
    .then(response => response.json())
    //update user state
    .then(data=>
      this.setState({
        users:data,
        isLoaded: false,
      })
    )
    //catch any errors and update
    .catch(error => this.setState({ error, isLoaded: false}));
  }


  componentDidMount(){
    this.fetchUsers();
  }

  render() {
    const { isLoaded, posts, error } = this.state;
    return (
    <React.Fragment>
    <h1>React Fetch - Blog</h1>
      <hr />
      {!isLoading ? Object.keys(posts).map(key => <Post key={key} body={posts[key]} />) : <h3>Loading...</h3>}
    </React.Fragment>
    );
  }

}
export default App;
