import React, {Component } from 'react'


class App extends Component {


  constructor(props) {
    super(props);
    this.state = {
      items: [],
      items2: [],
      isLoaded: false,
    }
  }

  /*
 deleteUser(email){
   fetch('https://01vablvh7h.execute-api.us-east-1.amazonaws.com/dev/deleteuser'+email,{
   })
 }
