import React, {Component} from 'react';

class App extends Component {

  constructor(props){
    super(props);
    this.state = {
      data: [],
      items:[],
      isLoaded: false,
    }
  }

  componentDidMount() {

    //for the users when they connect
    //fetch('https://4kflhc6oo7.execute-api.us-east-1.amazonaws.com/dev/')
    fetch('https://jsonplaceholder.typicode.com/users')
    .then(res => res.json())
    .then(json => {
      this.setState({
        data: [
          { id: "1", name: "user 1" },
        { id: "2", name: "user 2"},
        { id: "3", name: "user 3"}
        ],
        isLoaded: true,
        items: json,
      })



    });
    this.handleDeleteRow = this.handleDeleteRow.bind(this);
  }



    handleDeleteRow = (pid) => {
    alert(pid);
        let data = [...this.state.data];
        data.splice(data.findIndex(({ id }) => id === pid), 1);
        this.setState({ 
          data: data
        })

  }





  render() {
    let {isLoaded, items} = this.state;

    if (!isLoaded){
      return<div>Loading....</div>;
    }
    else{
        return (
          <div className="App">
            <ul>
              {items.map(item => (
                  <li key={item.id}>
                    Name: {item.name} | Email: {item.email}
                  </li>
              ))}

            </ul>

              <span>
              <label>
                <ul>
                  {this.state.data.map(person => (
                    <li key={person.id}>
                      {person.name}
                      <br />

        <button onClick={() => this.handleDeleteRow(person.id)} style={{color:'red'}}>Delete</button>
                    </li>
                  ))}
                </ul>
              </label>
            </span>
          </div>


        );

    }
}


}
export default App;
