import React, {Component} from 'react';

class App extends Component {

  constructor(props){
    super(props);
    this.state = {
      items:[],
      isLoaded: false,
    }
  }

  componentDidMount() {

    //for the users when they connect
    //fetch('https://4kflhc6oo7.execute-api.us-east-1.amazonaws.com/dev/')
    fetch('https://jsonplaceholder.typicode.com/users')
    .then(res => res.json())
    .then(json => {
      this.setState({
        isLoaded: true,
        items: json,
      })
    });

  }


  render() {
    let {isLoaded, items} = this.state;

    if (!isLoaded){
      return<div>Loading....</div>;
    }
    else{
        return (
          <div className="App">
            <ul>
              {items.map(item => (
                  <li key={item.id}>
                    Name: {item.name} | Email: {item.email}
                  </li>
              ))}

            </ul>

              <span>
              <label>
                <ul>
                  {this.state.data.map(person => (
                    <li key={person.id}>
                      {person.name}
                      <br />

        <button onClick={() => this.handleDeleteRow(person.id)} style={{color:'red'}}>Delete</button>
                    </li>
                  ))}
                </ul>
              </label>
            </span>
          </div>


        );

    }
}


}
export default App;
